mapper.insert:

	INSERT INTO howf(
		  midx
		, title
		, content
		, wdate
		, cnt
		, filename
		, cate
		, tag
		, delyn
	)
	VALUES(
		#{midx}
		, #{title}
		, #{content}
		, now()
		, 0
		, #{filename}
		, #{cate}
		, #{tag}
		, 'N'
	)


	SELECT max(hbidx) FROM howf

mapper.update:
UPDATE howf
   SET    title = #{title}
		, content = #{content}
		, cate = #{cate}
		, tag = #{tag}
		
		, filename = #{filename}
		
 WHERE hbidx = #{hbidx}

UPDATE howf
   SET delyn = 'Y'
 WHERE hbidx = #{hbidx}

mapper.delete:
DELETE FROM heart
 WHERE type = #{type}
   AND bidx = #{bidx}

mapper.insert:
INSERT INTO heart(
	  type
	, bidx
	, midx
)
VALUES(
	  #{type}
	, #{bidx}
	, #{midx}
)

mapper.delete:
DELETE FROM heart
WHERE type = #{type}
  AND bidx = #{bidx}
  AND midx = #{midx}

mapper.select:
SELECT COUNT(hidx)
 FROM heart
WHERE type = #{type}
  AND bidx = #{bidx}
  AND midx = #{midx}
