mapper.insert:
		
 			INSERT INTO CStable(
	 						origincsbidx
	 						,midx
	 						,title
	 						,divsn
	 						,content
	 						,filename
	 						,wdate
	 						,cnt
	 						,delyn
 						)
 						VALUES(						
	 						0
	 						,#{midx}
	 						,#{title}
	 						,#{divsn}
	 						,#{content}
	 						,#{filename}
	 						,now()
	 						,0
	 						,'N'
 						)
 		
		
			SELECT max(csbidx) FROM CStable

mapper.update:
UPDATE CStable
SET    origincsbidx = #{csbidx}
WHERE  csbidx = #{csbidx}

UPDATE CStable
SET	   cnt = cnt + 1
WHERE  csbidx = #{csbidx}

UPDATE CStable
SET    divsn = #{divsn}
	   ,title = #{title}
	   ,content = #{content}
	   ,filename = #{filename}
WHERE  csbidx = #{csbidx}

UPDATE CStable
SET    delyn = 'Y'
WHERE  origincsbidx = #{origincsbidx}

UPDATE CStable
SET    title = #{title}
	   ,content = #{content}
	   ,wdate = now()
WHERE  csbidx = #{csbidx}

UPDATE CStable
SET	   delyn = 'Y'
WHERE  csbidx = #{csbidx}
