mapper.insert:
	INSERT INTO notice(
				title
				,content
				,writer
				,midx
				,filename
				,wdate
				,cnt
				,delyn
				)
	VALUES	(
				#{title}
				,#{content}
				,#{writer}
				,#{midx}
				,#{filename}
				,now()
				,#{cnt}
				,'N'
	)
	

	SELECT max(nbidx) FROM notice

mapper.update:

	update notice
	SET title = #{title}
		,content = #{content}
		,filename = #{filename}
		 
	WHERE nbidx = #{nbidx}

	update notice
SET delyn = 'Y'
WHERE nbidx = #{nbidx}

mapper.select:
	select	nbidx
			,filename
	FROM	notice
	WHERE	nbidx = #{nbidx}
	AND		filename = #{filename}

mapper.update:
update notice
SET cnt = cnt+1
WHERE nbidx = #{nbidx}
