mapper.select:
SELECT COUNT(c.name) as cnt FROM (SELECT a.ridx , a.name , a.sidx FROM room a LEFT OUTER JOIN (SELECT r.ridx , rr.reidx FROM room r JOIN res rr ON r.ridx = rr.ridx WHERE rr.delyn = 'N'
AND rr.date1 <= #{date2} AND rr.date2 >= #{date1}
) b ON a.ridx = b.ridx WHERE b.reidx IS NULL) c LEFT OUTER JOIN stay d ON c.sidx = d.sidx WHERE d.sidx = #{sidx} AND c.name = #{name}

mapper.insert:

	INSERT INTO stay(
		midx
		, name
		, addr
		, detailaddr
		, contents
		, tag

		
		, photo
		
		
		, services
		, delyn
