		, #{name}
		, #{addr}
		, #{detailaddr}
		, #{contents}
		, #{tag}
		
		
		, #{photo}
		
		
		, #{services}
		, 'N'
	)


	SELECT max(sidx) FROM stay

INSERT INTO room(
