	, #{price}
	, #{tags}
	, #{people}
	, #{square}
	
	, #{photo}
	
	, #{sbed}
	, #{dbed}
	, #{qbed}
	, #{kbed}
	, 'N'
)

INSERT INTO comment(
	  content
	, star
	, type
	, midx
	, bidx
	, wdate
	, delyn
	
	, photo
	
)
VALUES(
	  #{content}
	, #{star}
	, 'stay'
	, #{midx}
	, #{bidx}
	, now()
	, 'N'
	
	, #{photo}
	
)

mapper.update:
UPDATE stay
SET   name = #{name}
	, addr = #{addr}
	, detailaddr = #{detailaddr}
	, contents = #{contents}
	, tag = #{tag}
	
	, photo = #{photo}
	
	, services = #{services}
WHERE sidx = #{sidx}

mapper.select:
SELECT COUNT(cbidx)
FROM comment
WHERE type = 'stay'
AND midx = #{midx}
AND bidx = #{bidx}
AND delyn = 'N'

mapper.update:
UPDATE comment
SET   content = #{content}
	, star = #{star}
	
	, photo = #{photo}
	
WHERE cbidx = #{cbidx}

UPDATE stay
SET delyn = 'Y'
WHERE sidx = #{sidx}

UPDATE comment
SET delyn = 'Y'
WHERE cbidx = #{cbidx}

mapper.insert:

	INSERT INTO res(
		  midx
		, ridx
		, date1
		, date2
		, merchant
		, pay
		, wdate
		, delyn
	)
	VALUES(
		  #{midx}
		, #{ridx}
		, #{date1}
		, #{date2}
		, #{merchant}
		, 'N'
		, now()
		, 'N'
	)


	SELECT max(reidx) FROM res

mapper.update:
UPDATE res
   SET pay = 'Y'
 WHERE merchant = #{merchant}

mapper.select:
SELECT r.ridx
	, r.merchant
FROM res r
JOIN room ro
ON r.ridx = ro.ridx
WHERE r.midx = #{midx}
AND ro.sidx = #{sidx}
AND ro.name = #{name}
AND ro.price = #{price}
AND r.pay = 'N'
AND r.delyn = 'N'

mapper.update:
UPDATE res
   SET delyn = 'Y'
 WHERE reidx = #{reidx}

mapper.insert:
INSERT INTO res(
	  midx
	, ridx
	, date1
	, date2
	, merchant
	, pay
	, wdate
	, delyn
)
VALUES(
	  #{midx}
	, #{ridx}
	, #{date1}
	, #{date2}
	, #{merchant}
	, 'N'
	, now()
	, 'N'
)

mapper.update:
UPDATE res
   SET delyn = 'Y'
 WHERE reidx = #{reidx}

mapper.select:
SELECT re.* , s.midx , s.name as sname FROM stay s JOIN (SELECT r.reidx , r.ridx , r.date1 , r.date2 , r.merchant , r.pay , r.wdate , r.delyn , ro.sidx , ro.name as rname FROM res r JOIN room ro ON r.ridx = ro.ridx WHERE r.delyn = 'N'
AND date1 >= #{date1}
AND date2 <= #{date2}
AND r.pay = 'Y') re ON s.sidx = re.sidx WHERE s.midx = #{midx} ORDER BY s.name asc, re.wdate desc
