mapper.insert:

	INSERT INTO team(
					midx
					,title
					,content
					,wdate
					,filename
					,applyyn
	)
	VALUES		(
					#{midx}
					,#{title}
					,#{content}
					,now()
					,#{filename}
					,#{applyyn}
				)
	
	
	SELECT max(tidx) FROM team

mapper.update:
UPDATE team
SET    cnt = cnt + 1
WHERE  tidx = #{tidx}

mapper.delete:
DELETE FROM jointable
WHERE		tidx = #{tidx}
AND			midx = #{midx}

mapper.select:
SELECT  a.jidx,b.joinyn FROM
(SELECT COUNT(jidx) as jidx, jidx as j
FROM   jointable
WHERE  tidx = #{tidx}
AND    midx = #{midx}
 ) a
LEFT OUTER JOIN
(SELECT joinyn, jidx
FROM jointable a
WHERE tidx = #{tidx}
AND midx = #{midx}
 ) b
ON a.j = b.jidx;

mapper.update:
UPDATE  team
SET		title = #{title}
		,content = #{content}
		,filename = #{filename}
		,applyyn = #{applyyn}
		,wdate = now()
WHERE	tidx = #{tidx}

UPDATE  team
SET		delyn = 'Y'
WHERE	tidx = #{tidx}

mapper.insert:

INSERT INTO recommend(
				tidx
				,midx
				,title
				,place
				,wdate
				,delyn
			)
VALUES		(
				#{tidx}
				,#{midx}
				,#{title}
				,#{place}
				,now()
				,'N'
			)


	SELECT max(ridx) FROM recommend

mapper.update:
UPDATE	recommend
SET		delyn = 'Y'
WHERE	ridx = #{ridx}
