mapper.update:
UPDATE usertable
   SET nickname = #{nickname}
 WHERE email = #{email}

mapper.select:
SELECT nickname
  FROM usertable
 WHERE email = #{email}

SELECT COUNT(email)
  FROM usertable
 WHERE email = #{email}

SELECT password
FROM usertable
WHERE email = #{email}

SELECT COUNT(nickname)
  FROM usertable
 WHERE nickname = #{nickname}

